mapper.select:
  		select p.hdzt,p.hdnf,p.hdsj,p.hdzzdw,p.hdxf,p.hdsj,a.zgh ,dz.rzzgmcm
		from app_bmpjxx a left join app_pxhd p on a.hdid=p.hdid
						  left join data_zwzc dz on a.zgh=dz.zgh 
  		where 1=1 


  				and p.hdnf=#{pjnf}





  					and p.hdsj >= '${time_start}'





  					and p.hdsj <= '${time_end}'





  				and dz.rzzgmcm=#{rzzgmcm}


  		limit  #{startRow,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(a.zgh) from app_bmpjxx a left join app_pxhd p on a.hdid=p.hdid left join data_zwzc dz on a.zgh=dz.zgh where 1=1
and p.hdnf=#{pjnf}
and p.hdsj >= '${time_start}'
and p.hdsj <= '${time_end}'
and dz.rzzgmcm=#{rzzgmcm}
